<?phpnamespace Database;use PDO;use PDOException;class Database{    private $host;    private $dbname;    private $user;    private $password;    private $connection;    public function __construct($host, $dbname, $user, $password)    {        $this->host = $host;        $this->dbname = $dbname;        $this->user = $user;        $this->password = $password;    }    public function connect()    {        try {            $this->connection = new PDO(                "mysql:host={$this->host};dbname={$this->dbname}",                $this->user,                $this->password            );            $this->connection->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);            return $this->connection;        } catch (PDOException $e) {            die("Database connection failed: " . $e->getMessage());        }    }}