<?phpnamespace Database;use PDO;use PDOException;class InitDatabase{    private ?PDO $db;    public function __construct(Database $db)    {        $this->db = $db->connect();    }    public function createTables()    {        $this->executeSqlFile(__DIR__ . '/sql/create_users_table.sql');        $this->executeSqlFile(__DIR__ . '/sql/create_links_table.sql');    }    private function executeSqlFile($filePath)    {        try {            $sql = file_get_contents($filePath);            if ($sql) {                $this->db->exec($sql);                echo "Executed SQL file: $filePath\n";            }        } catch (PDOException $e) {            die("Failed to execute SQL file $filePath: " . $e->getMessage());        }    }}