<?phpnamespace Models;use PDO;class Link extends Model{    public function __construct(PDO $db)    {        parent::__construct($db);    }    public function getAllLinksByUserId($userId)    {        $stmt = $this->db->prepare("SELECT * FROM links WHERE user_id = :user_id");        $stmt->bindParam(':user_id', $userId);        $stmt->execute();        return $stmt->fetchAll(PDO::FETCH_ASSOC);    }    public function create($userId, $originalUrl, $shortenedUrl)    {        $stmt = $this->db->prepare("INSERT INTO links (user_id, original_url, shortened_url) VALUES (:user_id, :original_url, :shortened_url)");        $stmt->bindParam(':user_id', $userId);        $stmt->bindParam(':original_url', $originalUrl);        $stmt->bindParam(':shortened_url', $shortenedUrl);        return $stmt->execute();    }    public function findOriginalUrlByShortened($shortenedUrl)    {        $stmt = $this->db->prepare("SELECT original_url FROM links WHERE shortened_url = :shortened_url");        $stmt->bindParam(':shortened_url', $shortenedUrl);        $stmt->execute();        $result = $stmt->fetch(PDO::FETCH_ASSOC);        return $result ? $result['original_url'] : null;    }    public function isValidUrl($url)    {        if (!filter_var($url, FILTER_VALIDATE_URL)) {            return false;        }        // Todo Check url is reachable by using curl        return true;    }}