<?phpnamespace Models;use PDO;class User extends Model{    public function __construct(PDO $db)    {        parent::__construct($db);    }    public function findByUsername($username)    {        $stmt = $this->db->prepare("SELECT * FROM users WHERE username = :username");        $stmt->bindParam(':username', $username);        $stmt->execute();        return $stmt->fetch(PDO::FETCH_ASSOC);    }    public function create($username, $password)    {        $hashedPassword = password_hash($password, PASSWORD_BCRYPT);        $stmt = $this->db->prepare("INSERT INTO users (username, password) VALUES (:username, :password)");        $stmt->bindParam(':username', $username);        $stmt->bindParam(':password', $hashedPassword);        return $stmt->execute();    }    public function updatePassword($id, $newPassword)    {        $hashedPassword = password_hash($newPassword, PASSWORD_BCRYPT);        $stmt = $this->db->prepare("UPDATE users SET password = :password WHERE id = :id");        $stmt->bindParam(':password', $hashedPassword);        $stmt->bindParam(':id', $id);        return $stmt->execute();    }    public function delete($id)    {        $stmt = $this->db->prepare("DELETE FROM users WHERE id = :id");        $stmt->bindParam(':id', $id);        return $stmt->execute();    }}