<?phprequire 'vendor/autoload.php';$routes = require 'Routes/web.php';$requestMethod = $_SERVER['REQUEST_METHOD'];$requestUri = parse_url($_SERVER['REQUEST_URI'], PHP_URL_PATH);function matchRoute($routes, $requestMethod, $requestUri) {    if (!isset($routes[$requestMethod])) {        return false;    }    foreach ($routes[$requestMethod] as $pattern => $controllerAction) {        $pattern = str_replace('/', '\/', $pattern); // Escape special characters for regex        if (preg_match("/^$pattern$/", $requestUri, $matches)) {            array_shift($matches); // Remove the full match from the beginning            return ['action' => $controllerAction, 'params' => $matches];        }    }    return false;}$match = matchRoute($routes, $requestMethod, $requestUri);if ($match) {    $action = $match['action'];    $params = $match['params'];    if (is_callable($action)) {        call_user_func_array($action, $params);    } else {        list($controller, $method) = $action;        if (class_exists($controller) && method_exists($controller, $method)) {            $controllerInstance = new $controller();            call_user_func_array([$controllerInstance, $method], $params);        } else {            echo "Controller or method not found.";        }    }} else {    echo "404 Not Found";}