<?phpclass Autoloader{    public static function register(): void    {        spl_autoload_register([__CLASS__, 'autoload']);    }    public static function autoload($class): void    {        $class = str_replace('\\', DIRECTORY_SEPARATOR, $class);        $file = __DIR__ . '/../' . $class . '.php';        if (file_exists($file)) {            require $file;        } else {            die("Class file for {$class} not found at {$file}");        }    }}// RegisterAutoloader::register();